import { useState } from 'react';
import { BrowserRouter as Router, Redirect, Route, Switch } from 'react-router-dom';
import Login from './components/Login';
import Signup from './components/Signup';
import Cards from './components/Cards';
// import ProtectedRoute from './ProtectedRoute';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  return (
    <Router>
      <Switch>
        <Route path="/signup" exact component={Signup} />
        <Route path={["/", "/login"]} exact render={props => !isAuthenticated ? (<Login{...props} setIsAuthenticated={setIsAuthenticated} />
        ) : (
          <Redirect to='/cards' />
        )
        } />
        {/* <ProtectedRoute path='/cards' isAuth={isAuthenticated} component={Cards}/> */}
        <Route path="/cards" exact render={props => !isAuthenticated ? (<Login{...props} setIsAuthenticated={setIsAuthenticated} />
        ) : (
          <Cards />
        )
        } />
      </Switch>
    </Router >
  );
}

export default App;
